    private class func imageForResource(resource: [UInt8]) -> UIImage {
        let imageData = NSData(bytesNoCopy: UnsafeMutablePointer<Void>(resource), length: resource.count, freeWhenDone: false)

        var image = UIImage(data: imageData, scale: 2)!
        let center = image.size.width / 2
        image = image.resizableImageWithCapInsets(UIEdgeInsets(top: center, left: center, bottom: center, right: center))
        
        return image
    }
    
    class func checkedImage() -> UIImage {
        return imageForResource(_checkedImage)
